import { useState, useEffect } from 'react'

function AddUsers({group}) {
    const [users, setUsers] = useState([])
    const fetchUsers = () => (
        fetch('/users').then(res => res.json()).then(data => setUsers(data))
    )
    useEffect(() => {fetchUsers()}, [])
    function handlePost(user_id, group) {
        return (
            console.log(user_id, group)
        )
    }
    const renderUsers = users.map(user => {
        return (
            <div key={user.id} >
            <ul className='usercard' >
                <li > {user.name} </li>
                <button className='button-30' onClick={() => handlePost(user.id)} > Add </button>
            </ul>
            </div>
        )
    })
    return (
        <div>
            <h1> Add Users </h1>
            <h4> {renderUsers} </h4>
        </div>
    )
}

export default AddUsers
