import { useState, useEffect } from 'react'
import { Link, Switch, Route } from 'react-router-dom'
import Requests from './Requests'

function Groups(){
    const [groups, setGroups] = useState([])
    const [groupID, setGroupID] = useState(null)

    useEffect(() => {
    fetch('/groups').then(res => res.json()).then(data => setGroups(data))
    }, [])

    const renderGroups = groups.map((group) => {
        setGroupID(group.id)
        return (
            <div>
            <ul className='groupcard' key={group.id}>
                {/* <li className='groupname'> {group.name} </li> */}
                <li key={group.id}>
                <Link to={`/requests/${group.id}`}> {group.name} </Link>
                </li>
            </ul>
            <Route path={'/requests'}>
                <Requests />
            </Route>
            </div>
            )
    })
    console.log(groupID)

    return (
        <div>
            <h1>Groups</h1>
            <div className='groupscontainer'> {renderGroups} </div>
        </div>
    )
}

export default Groups
