import React from "react";
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles({
    header: {
    //   position: 'relative',
    //   display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      flexDirection: 'column',
      padding: '15px 20px 18px 18px',
      borderRadius: '5px',
      backgroundImage: 'url("../Images/TopherEmby.png")',
      backgroundRepeat: 'no-repeat',
      backgroundPosition: ' center',
      backgroundSize: '70%',
      width: 'calc(70% - 10px)', 
      height: '300px',
      margin: '0 auto',
      zIndex: 1,
    },
  });

function Header() {
    const classes = useStyles();
  return (
  <div className={classes.header} style={{ backgroundImage: `url("https://github.com/Topher2014/TopherEmby/blob/main/logo.png?raw=true")` }}>
  <div className="hero-overlay bg-opacity-60"></div>
  <div className="hero-content text-center text-neutral-content">
    <div className="max-w-md">
      {/* <h1 className="mb-5 text-5xl font-bold">LUMOS</h1> */}
      {/* <p className="mb-5">chase the light</p> */}
      {/* <button className="btn btn-primary" style={{ backgroundColor: 'var(--primary-color)', borderColor: 'var(--primary-color)' }}>Search</button> */}
    </div>
  </div>
</div>
  );
}

export default Header;
