import { useHistory, Link, Route } from 'react-router-dom'
import { useState, useEffect, React } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup'

function EditGroups({groups, fetchGroups, setGroups}) {
    useEffect(() => {fetchGroups()}, [])
    const history = useHistory()
    const formSchema = yup.object().shape({
            name: yup.string().required('Enter group name, dummy!')
        })

    function handleDelete(id) {
        const newGroups = groups.filter(group => {
            return group.id !== id
        })
        console.log(id)
        fetch(`/group/${id}`, {
            method: 'DELETE'
        }).then(() => setGroups(newGroups))
    }

    const formik = useFormik({
        initialValues: {
            name: ''
        },
        // validationSchema: formSchema,
        // onSubmit: (values) => {
        //     fetch('/groups', {
        //         method: 'POST',
        //         headers: {
        //             'Content-Type': 'application/json',
        //         },
        //         body: JSON.stringify({...values}),
        //     }).then((response) => {
        //         if(response.ok) {
        //             response.json().then(() => {
        //                 // history.push('/editgroups')
        //                 fetchGroups()
        //                 // window.location.reload()
        //             })
        //         }
        //     })
        // }
    })
    // const [change, handleChange] = useState()
    const [name, setName] = useState('')
    const [formData, setFormData] = useState([])
        function handleChange(event){
            console.log(event.target.value)
            setName(event.target.value)
        }
        function handleSubmit(values){
            <h2 style={{color:'red'}}> {formik.errors.name} </h2>
            values.preventDefault()
            const data = {name: name}
            const array = [...formData, data]
            setFormData(array)
            console.log(values.target[0].value)
            // fetch('/groups', {
            //     method: 'POST',
            //     headers: {
            //         'Content-Type': 'application/json',
            //     },
            //     body: JSON.stringify({...values}),
            // }).then((response) => {
            //     if(response.ok) {
            //         response.json().then(() => {
            //             // history.push('/editgroups')
            //             fetchGroups()
            //             // window.location.reload()
            //         })
            //     }
            // })
        }

    const renderGroups = groups.map((group) => {
        return (
            <div key={group.id} >
            <ul className='groupcard' >
                <li key={group.id}> {group.name} </li>
                <button className='button-30' onClick={() => handleDelete(group.id)}> Delete </button>
            </ul>
            </div>
            )
    })
    return (
        <div>
            <h1> Edit Groups </h1>
            {/* <h2 style={{color:'red'}}> {formik.errors.name} </h2> */}
            {/* <form className='groupform' onSubmit={formik.handleSubmit}> */}
            <form className='groupform' onSubmit={handleSubmit}>
                <label> Name: </label>
                <textarea
                type='text'
                name='name'
                // value={formik.values.text}
                // onChange={formik.handleChange}
                value={name}
                onChange={handleChange}
                />
                <input className='button-30' type='submit' />
            </form>
            <div className='groupscontainer'> {renderGroups} </div>
        </div>
    )
}

export default EditGroups
