import { useEffect } from 'react'
import { Link, Route } from 'react-router-dom'
import Requests from './Requests'

function Groups({groups, fetchGroups}){
    useEffect(() => {fetchGroups()}, [])
    // const fetchGroupUsers = () => (
    //     fetch('/groupusers').then(res => res.json()).then(data => console.log(data))
    // )
    // useEffect(() => {
    //     fetchGroupUsers()
    // }, [])
    const mappedUsers = groups.map(group => group.groupuser.map(groupuser => groupuser.users))
    // console.log('mappedUsers:', mappedUsers)
    const mappedGroups = groups.map(group => group.groupuser.flat())
    console.log('mappedGroups:', mappedGroups)

    const renderGroups = groups.map((group) => {
        // console.log(group)
        // console.log(group.groupuser)
        return (
            <div key={group.id} >
            <ul className='groupcard' >
                <li >
                <Link to={`/groups/${group.id}/requests`}> {group.name} </Link>
                </li>
            </ul>
            </div>
            )
    })

    return (
        <div>
            <h1>Groups</h1>
            <Link to={`/editgroups`}> Edit Groups </Link>
            <div className='groupscontainer'> {renderGroups} </div>
            <Route path={`/groups/:groupId/requests`}>
                <Requests />
            </Route>
        </div>
    )
}

export default Groups
