import { useHistory, Link, Route } from 'react-router-dom'
import { useState, useEffect, React } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup'

function EditGroups({groups, fetchGroups, setGroups}) {
    useEffect(() => {fetchGroups()}, [])
    const [newGroup, setNewGroup] = useState([])
    const addGroup = (group) => setNewGroup(current => [...current, group])
        const formSchema = yup.object().shape({
            name: yup.string().required('Group name')
        })

    function handleDelete(id) {
        const newGroups = groups.filter(group => {
            return group.id !== id
        })
        console.log(id)
        fetch(`/group/${id}`, {
            method: 'DELETE'
        }).then(() => setGroups(newGroups))
    }

    const formik = useFormik({
        initialValues: {
            name: ' '
        },
        validationSchema: formSchema,
        onSubmit: (values) => {
            fetch('/groups', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({...values})
            }).then((response) => {
                if(response.ok) {
                    response.json().then(group => {
                        addGroup(group)
                        history.push('users')
                        window.location.reload()
                    })
                }
            })
        }
    })

    const renderGroups = groups.map((group) => {
        return (
            <div key={group.id} >
            <ul className='groupcard' >
                <li key={group.id}> {group.name} </li>
                <button class='button-30' onClick={() => handleDelete(group.id)}> Delete </button>
            </ul>
            </div>
            )
    })
    return (
        <div>
            <h1> Edit Groups </h1>
            <div className='groupscontainer'> {renderGroups} </div>
        </div>
    )
}

export default EditGroups
