import { useEffect } from 'react'
import { Link, Route } from 'react-router-dom'
import Requests from './Requests'

function Groups({groups, fetchGroups, user}){
    useEffect(() => {fetchGroups()}, [])

    // const filteredGroups = groups.map(mappedGroups => mappedGroups.groupuser).flat().filter(userGroups => userGroups.user_id === user.id)
    // console.log('filteredGroups:', filteredGroups)
    // console.log(user)

    const renderGroups = groups.map(mappedGroups => mappedGroups.groupuser).flat().filter(userGroups => userGroups.user_id === user.id).map(group => {
    // const renderGroups = filteredGroups.map((group) => {
        console.log(group)
        // console.log(group.groupuser)
        return (
            <div key={group.groups.id} >
            <ul className='groupcard' >
                <li >
                <Link to={`/groups/${group.groups.id}/requests`}> {group.groups.name} </Link>
                </li>
            </ul>
            </div>
            )
    })

    return (
        <div>
            <h1>Groups</h1>
            <Link to={`/editgroups`}> Edit Groups </Link>
            <div className='groupscontainer'> {renderGroups} </div>
            <Route path={`/groups/:groupId/requests`}>
                <Requests />
            </Route>
        </div>
    )
}

export default Groups
