import { useEffect, useState } from 'react'
import {Button, Container, TextField, Typography, Chip} from '@mui/material';
import { styled } from '@mui/system';

const GroupTitle = styled(Typography)({
  fontSize: 24,
  fontWeight: 'bold',
  marginBottom: 16,
});

const EditButton = styled(Button)({
  margin: '16px 0',
  fontWeight: 'bold',
  color: 'white',
  background: '#2e7d32',
  '&:hover': {
    background: '#1b5e20',
  },
});

const SelectBox = styled(TextField)({
  margin: '16px 0',
});

const GroupButton = styled(Button)({
  margin: '0 16px',
  fontWeight: 'bold',
});

function FriendsUsers({user, users, fetchUsers}) {
    useEffect(() => {fetchUsers()}, [])
    const [filteredUsers, setFilteredUsers] = useState([])
    useEffect(() => {setFilteredUsers(users)}, [users])

    function handleAddFriendClick(friend_id) {
        fetch('/friendships', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                user_id: parseInt(user.id),
                friend_id: parseInt(friend_id)
            })
        })
        .then(res => {
            if(res.ok) {
                window.location.reload(true)
            } else console.log('error adding friend')
        })
    }

    function handleDeleteFriendClick(friend_id) {
        fetch('/friendships', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                user_id: parseInt(user.id),
                friend_id: parseInt(friend_id)
            })
        })
        .catch(err => console.log(err))
        window.location.reload(true)
    }
    const [showFriends, setShowFriends] = useState(false)
    function handleClick() {
        setShowFriends((toggle) => !toggle)
    }
    const buttonText = showFriends ? <EditButton onClick={handleClick} > Show Friends </EditButton> : <EditButton onClick={handleClick} > Show All Users </EditButton>
    const searchText = showFriends ? 'Search All Users' : 'Search Friends' 

    const friendIDs = user.friends.map(friend => friend.id)
    const handleChange = (event) => {
        const search = event.target.value.toLowerCase()
        setFilteredUsers(users.filter(user => {
            const usernameBool = user.name.toLowerCase().includes(search)
            const emailBool = user.email.toLowerCase().includes(search)
            return usernameBool || emailBool
        }))
    }
    const renderUsers = filteredUsers.map(filteredUser => {
        const isFriend = friendIDs.includes(filteredUser.id)
        if (filteredUser.id === user.id){
            return ''
        }
        if (!showFriends){
            if (friendIDs.includes(filteredUser.id)){
            return (
            <Container key={filteredUser.id}>
                <br></br>
                {isFriend ? <Chip label={filteredUser.name} onDelete={() => handleDeleteFriendClick(filteredUser.id)} />  : <Chip label={filteredUser.name} onClick={() => handleAddFriendClick(filteredUser.id)} /> }
                <br></br>
            </Container>
            )
            }
            else return null
            }
        else if (showFriends){
            return (
            <Container key={filteredUser.id}>
                <br></br>
                {isFriend ? <Chip label={filteredUser.name} onDelete={() => handleDeleteFriendClick(filteredUser.id)} /> : <Chip label={filteredUser.name} onClick={() => handleAddFriendClick(filteredUser.id)} />}
                <br></br>
            </Container>
            )
            }
    return null
    })

    return (
        <Container sx={{marginTop: 10}}>
            <GroupTitle> Users and Friends </GroupTitle>
            {buttonText}
            <br></br>
            <br></br>
            <TextField label={searchText} onChange={(event) => handleChange(event)}/>  
            <br></br>
            <br></br>
            {renderUsers}
        </Container>
    )
}

export default FriendsUsers
