import { useHistory, Link, Route } from 'react-router-dom'
import { useState, useEffect, React } from 'react'
import { useFormik } from 'formik'
import * as yup from 'yup'

function EditGroups({groups, fetchGroups, setGroups}) {
    useEffect(() => {fetchGroups()}, [])
    const [test , setTest] = useState()
    const history = useHistory()
    const [newGroup, setNewGroup] = useState([])
    const addGroup = (group) => setNewGroup(current => [...current, group])
    const formSchema = yup.object().shape({
            text: yup.string().required('Group name')
        })

    function handleDelete(id) {
        const newGroups = groups.filter(group => {
            return group.id !== id
        })
        console.log(id)
        fetch(`/group/${id}`, {
            method: 'DELETE'
        }).then(() => setGroups(newGroups))
    }

    const formik = useFormik({
        initialValues: {
            name: ' '
        },
        validationSchema: formSchema,
        onSubmit: (values) => {
            console.log(values)
            fetch('/groups', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({...values}),
            }).then((response) => {
                if(response.ok) {
                    response.json().then(group => {
                        console.log(group)
                        addGroup(group)
                        history.push('/groups')
                        // window.location.reload()
                    setTest(values)
                    })
                }
            })
        }
    })
    // console.log(newGroup)
    // console.log(test)

    const renderGroups = groups.map((group) => {
        return (
            <div key={group.id} >
            <ul className='groupcard' >
                <li key={group.id}> {group.name} </li>
                <button className='button-30' onClick={() => handleDelete(group.id)}> Delete </button>
            </ul>
            </div>
            )
    })
    return (
        <div>
            <h1> Edit Groups </h1>
            <form className='groupform' onSubmit={formik.handleSubmit}>
                <label> Name: </label>
                <textarea
                type='text'
                name='name'
                value={formik.values.text}
                onChange={formik.handleChange}
                />
                <input className='button-30' type='submit' />
            </form>
            <div className='groupscontainer'> {renderGroups} </div>
        </div>
    )
}

export default EditGroups
