import {useState, useEffect} from 'react'
// import {ListItem, List, Container, Typography} from '@mui/material';
import { Container, Typography, List, ListItem, Box } from '@mui/material';

function Requests({ groupID }) {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    if (groupID)
      fetch(`/groups/${groupID}/requests`)
        .then((res) => res.json())
        .then((data) => setRequests(data));
  }, [groupID]);

  const renderRequests = requests.map((request) => {
    let type = '';
    if (request.type === 'movie') {
      type = request.type.charAt(0).toUpperCase() + request.type.slice(1);
    } else if (request.type === 'tv') {
      type = 'Show';
    }
    return (
      <ListItem key={request.id}>
        <Typography>
          {type} {request.name} {request.quality} {request.imdb_id}
        </Typography>
      </ListItem>
    );
  });

  return (
    <Container>
      <Typography sx={{ mb: 2 }}>
        Requests
      </Typography>
      {requests.length > 0 ? (
        <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
          {renderRequests}
        </List>
      ) : (
        <Box sx={{ mt: 2 }}>
          <Typography>No requests found.</Typography>
        </Box>
      )}
    </Container>
  );
}

export default Requests;
